import React from "react";
import { Link } from "react-router-dom";

export default function JobCard({ job }) {
  return (
    <div className="bg-white shadow-lg rounded p-4 mb-4">
      <h2 className="font-bold text-lg">{job.title}</h2>
      <p className="text-gray-600">{job.company}</p>
      <p className="text-gray-500">Location: {job.location} | Salary: {job.salary}</p>
      <Link
        to={`/jobs/${job._id}`}
        className="block mt-4 bg-blue-600 text-white text-center py-2 rounded hover:bg-blue-700"
      >
        Apply Job
      </Link>
    </div>
  );
}
