import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const EmployerDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const user = JSON.parse(atob(localStorage.getItem("token")));
        const res = await axiosInstance.get("/jobs");
        // Filter jobs posted by this employer
        const employerJobs = res.data.filter((job) => job.postedBy === user.id);
        setJobs(employerJobs);
      } catch (err) {
        console.error(err);
        alert("Error fetching your jobs");
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading your jobs...</p>;

  if (!jobs.length)
    return <p className="text-center mt-10">You have not posted any jobs yet.</p>;

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Your Posted Jobs</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {jobs.map((job) => (
          <div key={job._id} className="border rounded-lg p-4 shadow hover:shadow-lg bg-white transition">
            <h2 className="text-xl font-bold mb-2">{job.title}</h2>
            <p className="text-gray-600 mb-1"><strong>Company:</strong> {job.company}</p>
            <p className="text-gray-600 mb-1"><strong>Location:</strong> {job.location}</p>
            <p className="text-gray-600 mb-1"><strong>Salary:</strong> {job.salary || "N/A"}</p>
            <p className="text-gray-700 mb-3">{job.description}</p>

            <button
              onClick={async () => {
                try {
                  const res = await axiosInstance.get(`/jobs/applicants/${job._id}`);
                  alert(
                    `Applicants for ${job.title}:\n\n${res.data.applicants
                      .map((a) => a.name)
                      .join("\n") || "No applicants yet"}`
                  );
                } catch (err) {
                  console.error(err);
                  alert("Error fetching applicants");
                }
              }}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              View Applicants
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EmployerDashboard;
