import { useNavigate } from "react-router-dom";
import axios from "../utils/axiosInstance";

export default function JobCard({ job, user }) {
  const navigate = useNavigate();

  const handleApply = async () => {
    if (!user || user.role !== "candidate") {
      alert("Please login as candidate to apply for this job");
      return;
    }

    try {
      const res = await axios.post(`/job/apply/${job._id}`);
      alert(res.data.msg);
    } catch (err) {
      alert(err.response?.data?.msg || "Something went wrong");
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="font-bold text-lg cursor-pointer" onClick={() => navigate(`/job/${job._id}`)}>
        {job.title}
      </h2>
      <p>{job.company}</p>
      <p>{job.description}</p>
      <p>Location: {job.location} | Salary: {job.salary}</p>
      {user?.role === "candidate" && (
        <button
          onClick={handleApply}
          className="mt-4 w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Apply Job
        </button>
      )}
    </div>
  );
}
