import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const JobDetail = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);

  useEffect(() => {
    axiosInstance
      .get(`/jobs/${id}`)
      .then((res) => setJob(res.data))
      .catch((err) => console.log(err));
  }, [id]);

  const applyJob = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login to apply for jobs");
      return;
    }

    axiosInstance
      .post(`/jobs/apply/${id}`)
      .then((res) => alert(res.data.msg))
      .catch((err) => alert(err.response?.data?.msg || "Error applying"));
  };

  if (!job) return <div className="p-4 text-center">Loading job details...</div>;

  return (
    <div className="max-w-4xl mx-auto my-8 p-6 bg-white rounded shadow">
      <h1 className="text-3xl font-bold mb-4">{job.title}</h1>
      <p className="text-gray-700 mb-2">
        <strong>Company:</strong> {job.company}
      </p>
      <p className="text-gray-700 mb-2">
        <strong>Location:</strong> {job.location}
      </p>
      <p className="text-gray-700 mb-2">
        <strong>Salary:</strong> {job.salary || "N/A"}
      </p>
      <p className="text-gray-700 mb-4">
        <strong>Description:</strong> {job.description}
      </p>
      {job.skills && job.skills.length > 0 && (
        <p className="text-gray-700 mb-4">
          <strong>Skills Required:</strong> {job.skills.join(", ")}
        </p>
      )}
      <button
        onClick={applyJob}
        className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition"
      >
        Apply for this Job
      </button>
    </div>
  );
};

export default JobDetail;
