import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";

export default function JobDetail({ user }) {
  const { id } = useParams();
  const [job, setJob] = useState(null);

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await axios.get(`/job/${id}`);
        setJob(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchJob();
  }, [id]);

  if (!job) return <div className="p-6">Loading...</div>;

  return (
    <div className="p-6 max-w-2xl mx-auto bg-white rounded shadow">
      <h1 className="text-2xl font-bold mb-2">{job.title}</h1>
      <p className="mb-1"><strong>Company:</strong> {job.company}</p>
      <p className="mb-1"><strong>Description:</strong> {job.description}</p>
      <p className="mb-1"><strong>Location:</strong> {job.location}</p>
      <p className="mb-1"><strong>Salary:</strong> {job.salary}</p>
    </div>
  );
}
