import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const JobDetail = () => {
  const { id } = useParams();
  const [job, setJob] = useState({});
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchJob = async () => {
      const res = await axiosInstance.get(`/jobs/${id}`);
      setJob(res.data);
    };
    fetchJob();
  }, [id]);

  const applyJob = async () => {
    if (!token) return alert("Please login to apply for this job");
    try {
      const res = await axiosInstance.post(`/jobs/apply/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.msg);
    } catch (err) {
      alert(err.response.data.msg || "Application failed");
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-5 bg-white shadow rounded mt-5">
      <h2 className="text-2xl font-bold mb-3">{job.title}</h2>
      <p className="mb-1"><strong>Company:</strong> {job.company}</p>
      <p className="mb-1"><strong>Location:</strong> {job.location}</p>
      <p className="mb-1"><strong>Salary:</strong> {job.salary}</p>
      <p className="mb-1"><strong>Skills Required:</strong> {job.skills?.join(", ")}</p>
      <p className="mb-3">{job.description}</p>
      <button onClick={applyJob} className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">Apply Job</button>
    </div>
  );
};

export default JobDetail;
