import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const CandidateDashboard = () => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAppliedJobs = async () => {
      try {
        const user = JSON.parse(atob(localStorage.getItem("token")));
        const res = await axiosInstance.get(`/jobs/applied/${user.id}`);
        setAppliedJobs(res.data);
      } catch (err) {
        console.error(err);
        alert("Error fetching applied jobs");
      } finally {
        setLoading(false);
      }
    };

    fetchAppliedJobs();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading your applications...</p>;

  if (!appliedJobs.length)
    return <p className="text-center mt-10">You have not applied for any jobs yet.</p>;

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Your Applied Jobs</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {appliedJobs.map((job) => (
          <div key={job._id} className="border rounded-lg p-4 shadow hover:shadow-lg bg-white transition">
            <h2 className="text-xl font-bold mb-2">{job.title}</h2>
            <p className="text-gray-600 mb-1"><strong>Company:</strong> {job.company}</p>
            <p className="text-gray-600 mb-1"><strong>Location:</strong> {job.location}</p>
            <p className="text-gray-600 mb-1"><strong>Salary:</strong> {job.salary || "N/A"}</p>
            <p className="text-gray-700 mb-3">{job.description}</p>
            <p className="text-green-600 font-semibold">Status: Applied</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CandidateDashboard;
