import React, { useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("candidate");

  const handleRegister = (e) => {
    e.preventDefault();
    axiosInstance.post("/auth/register", { name, email, password, role })
      .then(res => alert("Registered successfully"))
      .catch(err => alert(err.response?.data?.msg || "Registration failed"));
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Register</h2>
      <form onSubmit={handleRegister} className="flex flex-col gap-2">
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} className="border p-2 rounded"/>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="border p-2 rounded"/>
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="border p-2 rounded"/>
        <select value={role} onChange={(e) => setRole(e.target.value)} className="border p-2 rounded">
          <option value="candidate">Candidate</option>
          <option value="employer">Employer</option>
        </select>
        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded mt-2">Register</button>
      </form>
    </div>
  );
};

export default Register;
