import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

toast.configure();

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const userId = "YOUR_CANDIDATE_USER_ID"; // Replace with logged-in candidate's ID

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/jobs");
      setJobs(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const applyJob = async (jobId) => {
    try {
      const res = await axios.post(`http://localhost:5000/api/jobs/apply/${jobId}`, {}, {
        headers: {
          // If using JWT token:
          // Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      toast.success(res.data.msg);
    } catch (err) {
      if (err.response) {
        toast.error(err.response.data.msg || "Failed to apply");
      } else {
        toast.error("Failed to apply");
      }
    }
  };

  return (
    <div className="grid grid-cols-3 gap-4 p-5">
      {jobs.length > 0 ? jobs.map((job) => (
        <div key={job._id} className="p-5 border rounded shadow">
          <h2 className="font-bold text-lg">{job.title}</h2>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary}</p>
          <button
            className="bg-green-500 text-white py-2 px-4 rounded mt-2"
            onClick={() => applyJob(job._id)}
          >
            Apply Job
          </button>
        </div>
      )) : <p>No jobs available</p>}
    </div>
  );
};

export default Home;
