import React from "react";

export default function Hero() {
  return (
    <section className="bg-gradient-to-r from-blue-600 to-blue-400 text-white py-24 px-4 rounded-lg mb-10">
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">
          Find Your Dream Job
        </h1>

        <p className="text-lg md:text-xl mb-6">
          Explore thousands of opportunities from top companies.
        </p>

        <button
          onClick={() => {
            const section = document.getElementById("jobs-section");
            if (section) section.scrollIntoView({ behavior: "smooth" });
          }}
          className="bg-white text-blue-600 font-bold px-6 py-3 rounded shadow hover:bg-gray-100 transition"
        >
          Browse Jobs
        </button>
      </div>
    </section>
  );
}
