import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const EmployerDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchJobs = async () => {
      const res = await axiosInstance.get("/jobs", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setJobs(res.data.jobs.filter(j => j.postedBy?._id === localStorage.getItem("userId")));
    };
    fetchJobs();
  }, [token]);

  return (
    <div className="p-5">
      <h2 className="text-xl font-bold mb-3">Your Posted Jobs</h2>
      <ul>
        {jobs.map(job => (
          <li key={job._id} className="mb-3 p-3 bg-white shadow rounded">
            <h3 className="font-bold">{job.title}</h3>
            <p>{job.company}</p>
            <p>{job.location}</p>
            <p>Applicants: {job.applicants.length}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployerDashboard;
