import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

export default function JobDetail({ user }) {
  const { id } = useParams();
  const [job, setJob] = useState(null);

  const fetchJob = async () => {
    try {
      const res = await axiosInstance.get(`/job/${id}`);
      setJob(res.data);
    } catch (err) {
      console.error(err);
      setJob(null);
    }
  };

  const applyJob = async () => {
    if (!user) return alert("Please login first");
    try {
      await axiosInstance.post(`/job/apply/${id}`);
      alert(`Application successful for ${job.title}`);
    } catch (err) {
      alert(err.response?.data?.msg || "Application failed");
    }
  };

  useEffect(() => {
    fetchJob();
  }, [id]);

  if (!job) return <div className="p-6">Loading...</div>;

  return (
    <div className="max-w-3xl mx-auto mt-8 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-2">{job.title}</h2>
      <p><b>Company:</b> {job.company}</p>
      <p><b>Location:</b> {job.location}</p>
      <p><b>Salary:</b> {job.salary}</p>
      <p><b>Skills Required:</b> {job.skills || "Not listed"}</p>
      <button className="bg-green-500 text-white px-4 py-2 rounded mt-4" onClick={applyJob}>
        Apply Job
      </button>
    </div>
  );
}
