import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";
import Hero from "../components/Hero";
import JobCard from "../components/JobCard";
import JobSearch from "../components/JobSearch";
import Pagination from "../components/Pagination";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // search state
  const [search, setSearch] = useState("");

  // pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const JOBS_PER_PAGE = 6;

  // Fetch all jobs on mount
  useEffect(() => {
    axiosInstance
      .get("/jobs")
      .then((res) => {
        setJobs(res.data);
        setFilteredJobs(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError("Unable to load jobs. Server might be down.");
        setLoading(false);
      });
  }, []);

  // Handle live search
  useEffect(() => {
    const value = search.toLowerCase();

    const filtered = jobs.filter(
      (job) =>
        job.title.toLowerCase().includes(value) ||
        job.company.toLowerCase().includes(value) ||
        job.location.toLowerCase().includes(value)
    );

    setFilteredJobs(filtered);
    setCurrentPage(1); // reset page on search
  }, [search, jobs]);

  // Pagination logic
  const indexOfLastJob = currentPage * JOBS_PER_PAGE;
  const indexOfFirstJob = indexOfLastJob - JOBS_PER_PAGE;
  const currentJobs = filteredJobs.slice(indexOfFirstJob, indexOfLastJob);

  const totalPages = Math.ceil(filteredJobs.length / JOBS_PER_PAGE);

  // Scroll to top when changing page
  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  if (loading)
    return (
      <p className="text-center mt-10 text-lg font-semibold">
        Loading jobs...
      </p>
    );

  if (error)
    return (
      <p className="text-center mt-10 text-red-500 text-lg">{error}</p>
    );

  return (
    <div className="p-4 bg-gray-100 min-h-screen">
      <Hero />

      {/* Search Component */}
      <div className="max-w-6xl mx-auto my-6">
        <JobSearch search={search} setSearch={setSearch} />
      </div>

      {/* Job Count */}
      <div className="max-w-6xl mx-auto mb-4 text-gray-700 font-medium">
        {filteredJobs.length} Jobs Found
      </div>

      {/* Job Grid */}
      <div id="jobs-section" className="max-w-6xl mx-auto">
        {currentJobs.length === 0 ? (
          <p className="text-center text-gray-600 mt-10">
            No jobs match your search.
          </p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {currentJobs.map((job) => (
              <JobCard key={job._id} job={job} />
            ))}
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            setPage={handlePageChange}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
