import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";
import { Link, useNavigate } from "react-router-dom";

const EmployerDashboard = () => {
  const navigate = useNavigate();

  const role = localStorage.getItem("role");
  const token = localStorage.getItem("token");

  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [salary, setSalary] = useState("");
  const [description, setDescription] = useState("");

  const [myJobs, setMyJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  const [error, setError] = useState(null);

  // Redirect if not employer
  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else if (role !== "employer") {
      navigate("/");
    }
  }, [role, token, navigate]);

  // LOAD EMPLOYER JOBS
  useEffect(() => {
    axiosInstance
      .get("/jobs/employer/myjobs")
      .then((res) => {
        setMyJobs(res.data);
        setLoading(false);
      })
      .catch((err) => {
        setError("Unable to load your jobs.");
        setLoading(false);
      });
  }, []);

  // CREATE JOB
  const handleSubmit = (e) => {
    e.preventDefault();

    axiosInstance
      .post("/jobs", {
        title,
        company,
        location,
        salary,
        description,
      })
      .then((res) => {
        alert("Job created successfully!");
        setMyJobs((prev) => [...prev, res.data]);

        // Reset fields
        setTitle("");
        setCompany("");
        setLocation("");
        setSalary("");
        setDescription("");
      })
      .catch((err) => {
        alert(err.response?.data?.msg || "Error creating job");
      });
  };

  // DELETE JOB
  const deleteJob = (jobId) => {
    if (!window.confirm("Are you sure you want to delete this job?")) return;

    axiosInstance
      .delete(`/jobs/${jobId}`)
      .then(() => {
        setMyJobs((prev) => prev.filter((job) => job._id !== jobId));
        alert("Job deleted successfully!");
      })
      .catch((err) => {
        alert(err.response?.data?.msg || "Failed to delete job");
      });
  };

  if (loading)
    return (
      <p className="text-center mt-10 font-semibold text-lg">
        Loading your dashboard...
      </p>
    );

  if (error)
    return (
      <p className="text-center mt-10 text-red-500 font-semibold">{error}</p>
    );

  return (
    <div className="max-w-5xl mx-auto p-6 mt-10">
      <h2 className="text-3xl font-bold mb-6">Employer Dashboard</h2>

      {/* CREATE JOB FORM */}
      <div className="bg-white p-6 rounded shadow mb-10">
        <h3 className="text-xl font-bold mb-4">Post a New Job</h3>

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder="Job Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-3 border rounded"
            required
          />

          <input
            type="text"
            placeholder="Company Name"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
            className="w-full p-3 border rounded"
            required
          />

          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="w-full p-3 border rounded"
            required
          />

          <input
            type="text"
            placeholder="Salary (optional)"
            value={salary}
            onChange={(e) => setSalary(e.target.value)}
            className="w-full p-3 border rounded"
          />

          <textarea
            placeholder="Job Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full p-3 border rounded"
            rows="4"
            required
          />

          <button className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
            Create Job
          </button>
        </form>
      </div>

      {/* MY JOBS LIST */}
      <h3 className="text-2xl font-bold mb-4">My Posted Jobs</h3>

      {myJobs.length === 0 ? (
        <p>You haven't posted any jobs yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {myJobs.map((job) => (
            <div key={job._id} className="bg-white shadow p-4 rounded border">
              <h4 className="text-xl font-bold">{job.title}</h4>
              <p className="text-gray-700">{job.company}</p>
              <p className="text-gray-700">{job.location}</p>

              <div className="flex gap-2 mt-3">
                <Link
                  to={`/job/${job._id}`}
                  className="flex-1 bg-blue-600 text-white text-center py-2 rounded hover:bg-blue-700"
                >
                  View Job
                </Link>

                <Link
                  to={`/employer/applicants/${job._id}`}
                  className="flex-1 bg-green-600 text-white text-center py-2 rounded hover:bg-green-700"
                >
                  View Applicants
                </Link>

                <button
                  onClick={() => deleteJob(job._id)}
                  className="flex-1 bg-red-600 text-white py-2 rounded hover:bg-red-700"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EmployerDashboard;
