import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";
import { Link, useNavigate } from "react-router-dom";

const CandidateDashboard = () => {
  const navigate = useNavigate();

  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role");
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // PROTECT ROUTE
  useEffect(() => {
    if (!token) navigate("/login");
    if (role !== "candidate") navigate("/");
  }, [token, role, navigate]);

  // LOAD JOB APPLICATIONS
  useEffect(() => {
    axiosInstance
      .get("/jobs/my-applications")
      .then((res) => {
        setAppliedJobs(res.data);
        setLoading(false);
      })
      .catch((err) => {
        setError("Unable to load your applications.");
        setLoading(false);
      });
  }, []);

  if (loading)
    return (
      <p className="text-center mt-10 text-lg font-semibold">
        Loading your applications...
      </p>
    );

  if (error)
    return (
      <p className="text-center mt-10 text-red-500 text-lg">{error}</p>
    );

  return (
    <div className="max-w-5xl mx-auto my-8 p-6 bg-gray-50 min-h-screen">
      {/* HEADER */}
      <div className="mb-10">
        <h1 className="text-3xl font-bold mb-2">Candidate Dashboard</h1>
        <p className="text-gray-700">Welcome, {user.name}</p>
        <p className="text-gray-700">{user.email}</p>
      </div>

      <h2 className="text-2xl font-bold mb-6">
        My Applications ({appliedJobs.length})
      </h2>

      {appliedJobs.length === 0 ? (
        <p className="text-gray-600">You haven't applied to any jobs yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {appliedJobs.map((app) => (
            <div
              key={app._id}
              className="border rounded-lg p-5 shadow bg-white hover:shadow-lg transition"
            >
              {/* JOB INFO */}
              <h2 className="text-xl font-bold mb-1">{app.job.title}</h2>
              <p className="text-gray-700">
                <strong>Company:</strong> {app.job.company}
              </p>
              <p className="text-gray-700">
                <strong>Location:</strong> {app.job.location}
              </p>
              <p className="text-gray-700 mb-2">
                <strong>Salary:</strong> {app.job.salary || "N/A"}
              </p>

              {/* APPLICATION META */}
              <p className="text-gray-600">
                <strong>Applied On:</strong>{" "}
                {new Date(app.createdAt).toLocaleDateString()}
              </p>
              <p className="text-green-600 font-semibold mb-3">
                Status: {app.status || "Applied"}
              </p>

              {/* COVER LETTER */}
              {app.coverLetter && (
                <div className="mb-3">
                  <strong>Cover Letter:</strong>
                  <p className="text-gray-700 whitespace-pre-line mt-1">
                    {app.coverLetter}
                  </p>
                </div>
              )}

              {/* RESUME DOWNLOAD */}
              {app.resume && (
                <a
                  href={`http://localhost:5000/${app.resume}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="block text-blue-600 underline mb-3"
                >
                  Download Resume
                </a>
              )}

              {/* VIEW JOB BUTTON */}
              <Link
                to={`/job/${app.job._id}`}
                className="block bg-blue-600 text-white text-center py-2 rounded hover:bg-blue-700 transition"
              >
                View Job
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CandidateDashboard;
